/** @jsx React.DOM */
var React 			= require('react')
 , 	MapItem	= require('app/components/map-item');

var _ = require('lodash');


module.exports = React.createClass({

  render: function () {
    var mapState = this.props.state.MapState;
    console.log('mapState', mapState);
    var mapMap, items;
    if (mapState.mapMap && mapState.mapMap.toJS) {
      mapMap = mapState.mapMap.toJS();
      items = Object.keys(mapMap).map(function (key) { return mapMap[key] });

    } else {
      items = [];
    }



    console.log('items', items)
  	console.log(this.props, 'props in map-list')
    var maps = items.map(function (map) {
      return (
      	<MapItem item={map} key={map.id} select={this.props.select} />
      )
    }.bind(this));

    return <ul>{maps}</ul>;
  }
});


