/** @jsx React.DOM */
var React 			= require('react')
 , 	AssignmentItem	= require('app/components/assignment-item')

module.exports = React.createClass({
  render: function () {
    var assignments = this.props.items.map(function (assignment) {
      return <AssignmentItem item={assignment} key={assignment.id} />;
    });

    return <ul>{assignments}</ul>;
  }
});


