/** @jsx React.DOM */
var React 			= require('react')
 , 	AssignmentItem	= require('app/components/assignment-item');

var _ = require('lodash');


module.exports = React.createClass({

  render: function () {
    var assignmentState = this.props.state.AssignmentState;
    console.log('assignmentState', assignmentState);
    var assignmentMap, items;
    if (assignmentState.assignmentMap && assignmentState.assignmentMap.toJS) {
      assignmentMap = assignmentState.assignmentMap.toJS();
      items = Object.keys(assignmentMap).map(function (key) { return assignmentMap[key] });

    } else {
      items = [];
    }



    console.log('items', items)
  	console.log(this.props, 'props in assignment-list')
    var assignments = items.map(function (assignment) {
      return (
      	<AssignmentItem item={assignment} key={assignment.id} select={this.props.select} />
      )
    }.bind(this));

    return <ul>{assignments}</ul>;
  }
});


