/** @jsx React.DOM */
var React       = require('react')
  , navigate    = require('react-mini-router').navigate;

module.exports = React.createClass({
  onSignInClicked: function (evt) {
    this.props.actions.signIn();
  },

  onTutorialClicked: function (evt) {
    chrome.tabs.create({ url: "http://youtu.be/u1auxOODJ88" });
  },

  render: function () {
    return <div className="wrap">
      <a  className="login button sign-in"
          onClick="this.onSignInClicked">
        Sign In
      </a>

      <a  className="tutorial"
          title="How to use Trailblazer"
          onClick={this.onTutorialClicked}>

        <img src="/src/ui/icons/tutorial-icon.svg" />
      </a>
    </div>;
  }
});
